import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {

    const [authState, setAuthState] = useState({
        isAuthenticated: false,
        username: null,
        token: null,
    });

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            // Если токен есть, восстанови состояние пользователя
            setAuthState({
                isAuthenticated: true,
                username: null, // Имя пользователя можно получить с сервера при необходимости
                token,
            });
        }
    }, []);

    const userLogin = ({ username, token }) => {
        localStorage.setItem('token', token); // Сохраняем токен
        setAuthState({
            isAuthenticated: true,
            username,
            token,
        });
    };

    const userLogout = () => {
        localStorage.removeItem('token'); // Удаляем токен
        setAuthState({
            isAuthenticated: false,
            username: null,
            token: null,
        });
    };

    return (
        <AuthContext.Provider value={{ authState, userLogin, userLogout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
