import React, { useState, useEffect } from 'react';
import TaskItem from './components/TaskItem';

function App() {
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        fetch('/api/tasks')
            .then(response => response.json())
            .then(data => setTasks(data))
            .catch(error => console.error('Error fetching tasks:', error));
    }, []);

    const addTask = (taskTitle) => {
        const newTask = { title: taskTitle, isCompleted: false, dueDate: null };
        fetch('/api/tasks', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newTask),
        })
            .then(response => response.json())
            .then(data => setTasks([...tasks, data]))
            .catch(error => console.error('Error adding task:', error));
    };

    const updateTask = (updatedFields) => {
        fetch(`/api/tasks/${updatedFields.id}`, {
            method: 'PATCH',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(updatedFields),
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(() => {
                setTasks(tasks.map(task =>
                    task.id === updatedFields.id ? { ...task, ...updatedFields } : task
                ));
            })
            .catch(error => console.error('Error updating task:', error));
    };

    const deleteTask = (id) => {
        fetch(`/api/tasks/${id}`, {
            method: 'DELETE',
        })
            .then(() => {
                setTasks(tasks.filter(task => task.id !== id));
            })
            .catch(error => console.error('Error deleting task:', error));
    };

    return (
        <div className="min-h-screen bg-gray-100 flex items-start justify-center pt-10">
            <div className="max-w-xl w-full bg-white p-6 rounded-lg shadow-md">
                <h1 className="text-2xl font-semibold text-gray-800 mb-4">To-Do List</h1>
                <div className="mb-4">
                    <input
                        type="text"
                        className="border border-gray-300 p-2 w-full rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
                        placeholder="Add a new task"
                        onKeyDown={(e) => {
                            if (e.key === 'Enter' && e.target.value.trim()) {
                                addTask(e.target.value);
                                e.target.value = '';
                            }
                        }}
                    />
                </div>
                <div className="space-y-2">
                    {tasks.map(task => (
                        <TaskItem
                            key={task.id}
                            task={task}
                            updateTask={updateTask}
                            deleteTask={deleteTask}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
}

export default App;
