import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import TaskInput from './components/TaskInput';
import TaskList from './components/TaskList';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import './App.css';
import ActivationPage from "./components/ActivationPage";

const App = () => {
    const [tasks, setTasks] = useState([]);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Получение задач из API при загрузке
    useEffect(() => {
        if (isAuthenticated) {
            fetch('/api/tasks')
                .then((response) => response.json())
                .then((data) => setTasks(data))
                .catch((error) => console.error('Error fetching tasks:', error));
        }
    }, [isAuthenticated]);

    // Добавление задачи
    const addTask = (task) => {
        fetch('/api/tasks', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(task),
        })
            .then((response) => response.json())
            .then((newTask) => setTasks([...tasks, newTask]))
            .catch((error) => console.error('Error adding task:', error));
    };

    const updateTask = (updatedFields) => {
        fetch(`/api/tasks/${updatedFields.id}`, {
            method: 'PATCH',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(updatedFields),
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.text().then((text) => (text ? JSON.parse(text) : {}));
            })
            .then(() => {
                setTasks(
                    tasks.map((task) =>
                        task.id === updatedFields.id ? { ...task, ...updatedFields } : task
                    )
                );
            })
            .catch((error) => console.error('Error updating task:', error));
    };

    // Удаление задачи
    const deleteTask = (taskId) => {
        fetch(`/api/tasks/${taskId}`, { method: 'DELETE' })
            .then(() => {
                setTasks(tasks.filter((task) => task.id !== taskId));
            })
            .catch((error) => console.error('Error deleting task:', error));
    };

    const handleLogin = (credentials) => {
        // Пример аутентификации (замените на реальную логику)
        if (credentials.username === 'user' && credentials.password === 'password') {
            setIsAuthenticated(true);
        } else {
            alert('Неверные данные для входа!');
        }
    };

    return (
        <Router>
            <Routes>
                <Route
                    path="/"
                    element={
                        isAuthenticated ? (
                            <div className="app">
                                <h1>To-Do List</h1>
                                <TaskInput addTask={addTask} />
                                <TaskList tasks={tasks} updateTask={updateTask} deleteTask={deleteTask} />
                            </div>
                        ) : (
                            <Navigate to="/login" />
                        )
                    }
                />
                <Route path="/activate" element={<ActivationPage />} />
                <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
                <Route path="/register" element={<RegisterPage onRegister={(user) => console.log('Registering:', user)} />} />
            </Routes>
        </Router>
    );
};

export default App;
