import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import TaskInput from '../TaskInput';
import TaskList from '../TaskList';
import { fetchTasks, createTask, updateTaskById, deleteTaskById } from '../../api/taskApi'; // API-запросы
import { useAuth } from '../AuthContext'; // Контекст авторизации

const TaskPage = () => {
    const [tasks, setTasks] = useState([]);
    const { authState } = useAuth(); // Проверяем авторизацию и получаем токен
    const navigate = useNavigate();

    // Получение задач пользователя при загрузке
    useEffect(() => {
        if (!authState.isAuthenticated) {
            navigate('/login'); // Перенаправляем на логин, если не авторизованы
            return;
        }

        fetchTasks(token)
            .then((data) => setTasks(data))
            .catch((error) => console.error('Error fetching tasks:', error));
    }, [authState, token, navigate]);

    // Добавление задачи
    const addTask = (task) => {
        createTask(task, token)
            .then((newTask) => setTasks([...tasks, newTask]))
            .catch((error) => console.error('Error adding task:', error));
    };

    // Обновление задачи
    const updateTask = (updatedFields) => {
        updateTaskById(updatedFields.id, updatedFields, token)
            .then(() => {
                setTasks(tasks.map((task) =>
                    task.id === updatedFields.id ? { ...task, ...updatedFields } : task
                ));
            })
            .catch((error) => console.error('Error updating task:', error));
    };

    // Удаление задачи
    const deleteTask = (taskId) => {
        deleteTaskById(taskId, token)
            .then(() => setTasks(tasks.filter((task) => task.id !== taskId)))
            .catch((error) => console.error('Error deleting task:', error));
    };

    return (
        <div className="app">
            <h1 className="text-2xl font-bold mb-4">To-Do List</h1>
            <TaskInput addTask={addTask} />
            <TaskList tasks={tasks} updateTask={updateTask} deleteTask={deleteTask} />
        </div>
    );
};

export default TaskPage;
