// App.jsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import TaskPage from './components/pages/TaskPage';
import LoginPage from './components/pages/LoginPage';
import RegisterPage from "./components/pages/RegisterPage"; // Пример страницы входа

const App = () => {
    const isAuthenticated = !!localStorage.getItem('token'); // Пример проверки аутентификации

    return (
        <Router>
            <div className="app">
                <Routes>
                    <Route path="/" element={isAuthenticated ? <TaskPage /> : <Navigate to="/login" />} />
                    <Route path="/login" element={<LoginPage />} />
                    <Route path="/register" element={<RegisterPage />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
