import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ReactCodeInput from 'react-code-input';

const ActivationPage = () => {
    const [activationCode, setActivationCode] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    const handleActivation = () => {
        fetch('/api/v1/auth/activate-account', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ code: activationCode }),
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Неверный код активации');
                }
                return response.json();
            })
            .then(() => {
                // Успешная активация, перенаправляем на страницу логина
                navigate('/login');
            })
            .catch(error => {
                setErrorMessage(error.message);
            });
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
            <div className="bg-white p-6 rounded shadow-md w-80">
                <h1 className="text-xl font-bold mb-4 text-center">Активация аккаунта</h1>
                <p className="text-sm text-gray-600 mb-4 text-center">
                    Введите код активации, отправленный на вашу почту.
                </p>
                <ReactCodeInput
                    type="number"
                    fields={6}
                    onChange={(value) => setActivationCode(value)}
                    inputStyle={{
                        margin: '4px',
                        width: '40px',
                        height: '40px',
                        border: '1px solid #ccc',
                        borderRadius: '4px',
                        textAlign: 'center',
                        fontSize: '18px',
                    }}
                />
                {errorMessage && (
                    <p className="text-red-500 text-sm mt-2 text-center">{errorMessage}</p>
                )}
                <button
                    onClick={handleActivation}
                    className="mt-4 bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded w-full"
                >
                    Активировать
                </button>
            </div>
        </div>
    );
};

export default ActivationPage;
