// App.jsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import TaskPage from './components/pages/TaskPage';
import LoginPage from './components/pages/LoginPage';
import RegisterPage from "./components/pages/RegisterPage";
import ActivationPage from "./components/pages/ActivationPage"; // Пример страницы входа
import {useAuth} from "./components/AuthContext";

const App = () => {
    const { authState } = useAuth(); // Пример проверки аутентификации

    return (
        <Router>
            <div className="app">
                <Routes>
                    <Route path="/" element={authState.isAuthenticated ? <TaskPage /> : <Navigate to="/login" />} />
                    <Route path="/login" element={<LoginPage />} />
                    <Route path="/register" element={<RegisterPage />} />
                    <Route path="/activate" element={<ActivationPage />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
