import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import { authenticate } from '../../api/authApi';

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    const { userLogin } = useAuth();

    const handleLogin = async () => {
        try {
            const data = await authenticate(username, password);
            userLogin({ username, token: data.token });
            navigate('/');
        } catch (error) {
            setErrorMessage(error.response?.data?.validationErrors || 'Ошибка входа');
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="bg-white p-8 rounded shadow-md w-96">
                <h1 className="text-2xl font-bold mb-4 text-center">Вход</h1>
                <input
                    type="text"
                    placeholder="Имя пользователя"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className="w-full p-2 mb-4 border border-gray-300 rounded"
                />
                <input
                    type="password"
                    placeholder="Пароль"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="w-full p-2 mb-4 border border-gray-300 rounded"
                />
                {errorMessage.password && (
                    <p className="text-red-500 text-sm mb-4 text-center">{errorMessage.password}</p>
                )}
                <button
                    onClick={handleLogin}
                    className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
                >
                    Войти
                </button>
                <p className="mt-3 text-center text-gray-600">
                    Нет аккаунта?{' '}
                    <button
                        onClick={() => navigate('/register')}
                        className="text-blue-500 hover:underline"
                    >
                        Зарегистрироваться
                    </button>
                </p>
            </div>
        </div>
    );
};

export default LoginPage;
