import React, { useState } from 'react';

const TaskItem = ({ task, updateTask, deleteTask }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editedText, setEditedText] = useState(task.title);

    const handleCheckboxChange = () => {
        updateTask({ id: task.id, isCompleted: !task.isCompleted });
    };

    const handleDueDateChange = (e) => {
        updateTask({ id: task.id, dueDate: e.target.value });
    };

    const handleTextClick = () => {
        setIsEditing(true);
    };

    const handleTextChange = (e) => {
        setEditedText(e.target.value);
    };

    const handleTextBlur = () => {
        setIsEditing(false);
        if (editedText !== task.title) {
            updateTask({ id: task.id, title: editedText });
        }
    };

    const handleTextKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleTextBlur();
        }
    };

    return (
        <div className="flex items-center justify-between p-2 bg-gray-50 rounded-lg border border-gray-200">
            <input
                type="checkbox"
                checked={task.isCompleted}
                onChange={handleCheckboxChange}
                className="mr-2 h-5 w-5 text-blue-600 rounded"
            />
            {isEditing ? (
                <input
                    type="text"
                    value={editedText}
                    onChange={handleTextChange}
                    onBlur={handleTextBlur}
                    onKeyPress={handleTextKeyPress}
                    className="flex-1 p-1 border-b border-gray-300 focus:outline-none"
                    autoFocus
                />
            ) : (
                <span
                    onClick={handleTextClick}
                    className={`flex-1 ${task.isCompleted ? 'line-through text-gray-400' : 'text-gray-800'}`}
                >
          {task.title}
        </span>
            )}
            <div className="flex flex-col items-center ml-4">
                <label className="text-xs text-gray-500">Due date</label>
                <input
                    type="date"
                    value={task.dueDate || ''}
                    onChange={handleDueDateChange}
                    className="border border-gray-300 p-1 rounded-md text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-400"
                />
            </div>
            <button
                onClick={() => deleteTask(task.id)}
                className="rounded-md bg-zinc-500 p-2.5 border border-transparent text-center text-sm text-white transition-all shadow-sm hover:shadow-lg focus:bg-slate-700 focus:shadow-none active:bg-slate-700 hover:bg-slate-700 active:shadow-none disabled:pointer-events-none disabled:opacity-50 disabled:shadow-none"
                type="button">
                <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="20" height="20" viewBox="0 0 24 24">
                    <path
                        d="M 10 2 L 9 3 L 4 3 L 4 5 L 5 5 L 5 20 C 5 20.522222 5.1913289 21.05461 5.5683594 21.431641 C 5.9453899 21.808671 6.4777778 22 7 22 L 17 22 C 17.522222 22 18.05461 21.808671 18.431641 21.431641 C 18.808671 21.05461 19 20.522222 19 20 L 19 5 L 20 5 L 20 3 L 15 3 L 14 2 L 10 2 z M 7 5 L 17 5 L 17 20 L 7 20 L 7 5 z M 9 7 L 9 18 L 11 18 L 11 7 L 9 7 z M 13 7 L 13 18 L 15 18 L 15 7 L 13 7 z"></path>
                </svg>
            </button>
        </div>
    );
};

export default TaskItem;
